  const [busqueda,guardarBusqueda] = useState('');

  useEffect(() => {
    const consultarAPI = async() =>{
      if(busqueda==='') return; // No trae valores en la primera

      const imagenesPorPagina = 10;
      const key= '1732750-d45b5378879d1e877cd1d35a6';
      const url = 'https://pixabay.com/api/?key=${key}&q=${busqueda}';

    }
    consultarAPI();
  }, [busqueda]);

  return (

    <div className="container">
      <div className="jumbotron">
        <p className="lead text-center">Buscador de Imagenes
        </p>

        <Formulario
          guardarBusqueda={guardarBusqueda}
        />

      </div>
    </div>

  );
}

export default App;
